mapper.select:
select i.num as num, i.name as name, i.item_type as item_type, i.cust_type as cust_type, i.price as price, i.disc_price as disc_price, i.main_img as main_img, i.review_cnt as review_cnt, i.review_rate as review_rate, i.like_cnt as like_cnt, i.col as col from item i inner join item_state s on i.num = s.num inner join item_detail d on i.num = d.num where true
AND i.name LIKE concat('%', #{nameKeyword}, '%') AND i.item_type LIKE concat('%', #{typeKeyword}, '%')
AND i.name LIKE concat('%', #{nameKeyword}, '%') AND i.cust_type = #{custKeyword}
AND i.item_type LIKE concat('__', #{typeKeyword}, '%') AND i.cust_type = #{custKeyword}
AND i.item_type LIKE concat(#{typeKeyword}, '%')
AND i.name LIKE concat('%', #{nameKeyword}, '%') AND i.item_type LIKE concat('__', #{typeKeyword}, '%') AND i.cust_type = #{custKeyword}
order by d.rel_date desc, s.sales_qty desc, i.review_cnt desc, i.like_cnt desc limit #{offset}, #{pageSize}

select count(*) from item i where true
AND i.name LIKE concat('%', #{nameKeyword}, '%') AND i.item_type LIKE concat('%', #{typeKeyword}, '%')
AND i.name LIKE concat('%', #{nameKeyword}, '%') AND i.cust_type = #{custKeyword}
AND i.item_type LIKE concat('__', #{typeKeyword}, '%') AND i.cust_type = #{custKeyword}
AND i.item_type LIKE concat(#{typeKeyword}, '%')
AND i.name LIKE concat('%', #{nameKeyword}, '%') AND i.item_type LIKE concat('__', #{typeKeyword}, '%') AND i.cust_type = #{custKeyword}

mapper.update:
update item
set name = #{name}, price = #{price}, disc_price = #{disc_price}, main_img = #{main_img}, review_cnt = #{review_cnt}, like_cnt = #{like_cnt}, review_rate = #{review_rate}, col = #{col}, reg_date = now(), reg_id = 'manage', up_date = now(), up_id = 'manager', item_type = #{item_type}, cust_type = #{cust_type}
where num = #{num};
